sendData = () => {
  var body = JSON.parse(data.Body);
  var queryString = body.Request.queryString != null ? "?" + body.Request.queryString : "";
  var location = "--location '" + body.Request.url + queryString + "' \\\n";
  var headers = "";
  Object.entries(body.Request.header).forEach(entry => {
    headers += "--header '" + entry[0] + ":" + entry[1] + "'\\\n";
  });
  var dataRaw = "--data-raw '" + JSON.stringify(body.Request.body) + "'";
  var cUrl = "curl " + location + headers + dataRaw;
  navigator.clipboard.writeText(cUrl);
  alert("Copiado para o clipboard");


